import Header from './Header.js';
import Footer from './Footer.js'
import Main from './Main.js'
import Gameend from './Gameend.js'
import React, { useState, useRef } from "react";


const App = () => {
  const [score, setScore] = useState(0)
  const [isGameOver, setIsGameOver] = useState(false)

  const childMain = useRef(null)

  const handleIsGameOver = (newIsGameOver) => {
    setIsGameOver(newIsGameOver)
  }
  const handleSetScore = (newScore) => {
    setScore(newScore)
  }
  const callMainFunctionNewGame = () => {
    if (childMain.current) {
      childMain.current.newGame()
  }
}

  return (
    <div>
    <Header />
    <Main ref ={(ref) => (childMain.current = ref)} score = {score} setScore={handleSetScore} isGameOver = {isGameOver} setIsGameOver = {handleIsGameOver}/>
    <Gameend score={score} isGameOver = {isGameOver} setIsGameOver = {handleIsGameOver} callMainFunctionNewGame = {callMainFunctionNewGame}/>
    <Footer />
    </div>
)
}


export default App
