import './Style.css';
import React, { useState, useRef, useEffect } from "react";





const Main = () => {

const [operation, setOperation] = useState("")
const [integer1, setInteger1] = useState("")
const [integer2, setInteger2] = useState("")
const [integer3, setInteger3] = useState("")
const [answer, setAnswer] = useState(null)
const [userAnswer, setUserAnswer] = useState("")
const [time, setTime] = useState(60)
const intervalRef = useRef(null)
const [level, setLevel] = useState(1)
const [lives, setLives] = useState(3)
const [score, setScore] = useState(0)
const [isGameOver, setIsGameOver] = useState(false)


function randint() {
    return Math.floor(Math.random()*10**(level))
}

function randinteger1() {
    let randinteger1 = randint()
    console.log(randinteger1)
    setInteger1(randinteger1)
    return randinteger1
}

function randinteger2() {
    let randinteger2 = randint()
    console.log(randinteger2)
    setInteger2(randinteger2)
    return randinteger2
}

const handleChange = event => {
    setUserAnswer(event.target.value)
}

const newGame = () =>{
    setTime(60)
    setLives(3)
    setAnswer(null)
    setUserAnswer("")
    setScore(0)
    const userInputElement = document.getElementById('userAnswer');
    userInputElement.focus();
    if (intervalRef.current){
        clearInterval(intervalRef.current)
    }
    setIsGameOver(false)
    randomQuestion()
    intervalRef.current = setInterval(reduceTime,1000)

}

function randomQuestion() {
        let operationChance = (Math.floor(Math.random()*3))

        /*This needs changing to 4 to include division, use a UseEffect or something similar */

        if (operationChance === 0){
            setOperation(" + ")
            setAnswer(randinteger1() + randinteger2())
        } 
        if (operationChance === 1){
            setOperation(" - ")
            setAnswer(randinteger1() - randinteger2())
        }
        if (operationChance === 2){
            setOperation(" x ")
            setAnswer(randinteger1() * randinteger2())        }
        if (operationChance === 3){
            setOperation(" / ")
            setAnswer(randinteger1() / randinteger2())
        }
        console.log(answer)
        return
    }

function checkAnswer() {

    if (userAnswer == answer){
        setScore(score+1)
        console.log("correct")
        setUserAnswer("")
        randomQuestion()
    } else {
        console.log("wrong answer")
        setLives(lives-1)
        setUserAnswer("")
        randomQuestion()

}
}
/*this next part is listening to see if lives falls to 0 */

useEffect(() =>{
    if (lives === 0){
        setIsGameOver(true)
        clearInterval(intervalRef.current)
    }
    }, [lives])        


const gameOver = () => {
    return(
        <div className = {`gameOver ${isGameOver ? "slide-in" : "slide-out"}`}>
            <h1>Game Over! Score: {score}</h1>
           <button onClick={newGame}>PLAY AGAIN</button>
        </div> 
    )
}


const reduceTime = () => {
    setTime((prevTime) => (prevTime > 0 ? prevTime-1 : 0))
    if (time === 0){
        setIsGameOver(true);
        clearInterval(intervalRef.current)

    }
}


document.onkeydown = (event) => {
    if (event.key === "Enter") {
    checkAnswer()
    }
}

    return (
        <div className="main">
            {isGameOver && gameOver()}
            <button onClick={newGame}>New Game</button>
            <h2>Level: {level}</h2>
            <h3>{integer1}{operation}{integer2}</h3>
            <input type="text" id="userAnswer" name="userAnswer" onChange={handleChange} value={userAnswer}/>
            <button type="submit" onClick={() => checkAnswer(userAnswer, answer) }>Submit</button>
            <h4>Score: {score}</h4>
            <h2>Time remaining: {time}</h2>
            <h2>Lives: {lives}</h2>


        </div>
    )
}

export default Main
