import './Style.css';
import React, { useState } from "react";





export default function Main () {

const [operation, setOperation] = useState("+")
const [integer1, setInteger1] = useState(1)
const [integer2, setInteger2] = useState(1)

function randint() {
    return Math.floor(Math.random()*10)
}

function randinteger1() {
    let randinteger1 = randint()
    console.log(randinteger1)
    setInteger1(randinteger1)
    return randinteger1
}

function randinteger2() {
    let randinteger2 = randint()
    console.log(randinteger2)
    setInteger2(randinteger2)
    return randinteger2
}

var answer = ""
function randomQuestion() {
        let operationChance = (Math.floor(Math.random()*4))

        if (operationChance === 0){
            setOperation("+")
            answer = randinteger1() + randinteger2()
        } 
        if (operationChance === 1){
            setOperation("-")
            answer = randinteger1() - randinteger2()
        }
        if (operationChance === 2){
            setOperation("x")
            answer = randinteger1() * randinteger2()
        }
        if (operationChance === 3){
            setOperation("/")
            answer = randinteger1() * randinteger2()
        }
        if (answer % 0 !== 1){
            randomQuestion()
        }

        console.log(answer)
        return answer
    }

    return (
        <div className="main">
            <button onClick={randomQuestion}>New Question</button>
            <h3>{integer1}{operation}{integer2}</h3>
            <h4></h4>

        </div>
    )
}
