import './Style.css';
import React, { useState } from "react";




export default function Main () {

const [operation, setOperation] = useState("")
const [integer1, setInteger1] = useState("")
const [integer2, setInteger2] = useState("")



function randomQuestion() {
        let randinteger1 = Math.floor(Math.random*10)
        setInteger1(randinteger1)
        let randinteger2 = Math.floor(Math.random*10)
        setInteger2(randinteger2)
        let operationChance = (Math.floor(Math.random*4))
        let result
        if (operationChance === 0){
            setOperation("+")
            result = integer1 + integer2
        } 
        if (operationChance === 1){
            setOperation("-")
            result = integer1 - integer2
        }
        if (operationChance === 2){
            setOperation("x")
            result = integer1 * integer2
        }
        if (operationChance === 3){
            setOperation("/")
            result = integer1 / integer2
        }   
        return result
    }

    return (
        <div className="main">
            <button onClick={randomQuestion}>New Question</button>
            <h3>{this.state.integer1}{this.state.operation}{this.state.integer2}</h3>

        </div>
    )
}
